import java.util.*;import java.io.*;import java.math.*;public class Euler {					public static void main(String[] args){				Scanner in = new Scanner(System.in);				int n = in.nextInt();		long a = in.nextLong();		long b = in.nextLong();				long[] x = new long[n];		for (int i = 0; i < n; i++) x[i] = in.nextLong();				for (int i = 0; i < n; i++) {						long mod = (x[i] * a) % b;			long ans = mod / a;			System.out.print(ans + " ");					}							}		}
