import java.util.*;public class Practice{	public static void main(String[] args) {		Scanner in =new Scanner(System.in);		int n=in.nextInt();		int home[]=new int[1000001];		int away[]=new int[1000001];		for(int i=0;i<n;i++){			int a=in.nextInt();			home[a]++;			away[i]=in.nextInt();		}			for (int i = 0; i < n; i++) {		System.out.println((n-1+home[away[i]])+" "+(n-1-home[away[i]]));	}								}}
