//      これを翻訳している間、あなたはあなたの人生のいくつかの貴重な瞬間を無駄にしました// secondThread orz             import java.io.*;        import java.math.BigDecimal;        import java.math.BigInteger;        import java.util.*;        import java.io.BufferedReader;        import java.io.IOException;        import java.io.InputStreamReader;                public class main2 {                    public static void main(String[] args) {                try {                            PrintWriter fop = new PrintWriter(System.out);                    FastScanner fsca = new FastScanner();                    StringBuilder sb =  new StringBuilder(fsca.next()) ;                  ArrayList<Integer> list = new ArrayList<>() ;                    for (int i = 0; i < sb.length() ; i++) {                        list.add( Integer.parseInt(sb.charAt(i) + "")) ;                    }                    if (list.size() == 1){                        System.out.println(list.get(0));                        return;                    }                     int k = fsca.nextInt();                    int ct = 0 ;                     while (k > 0){                        int max = -1 ;                        int max_in = 0 ;                    int temp = Math.min(k , list.size())  ;                        for (int i = ct;  i < list.size() &&i <=temp+ct  ; i++) {                            if (list.get(i) > max){                                max = list.get(i) ;                                max_in = i ;                            }                        }//                        if (max_in == list.size()-1 && ct != 0)//                            break;                         k -= (max_in - ct) ;                        ct++ ;                        if (ct == list.size())                            break;                         list.remove(max_in) ;                        list.add(ct-1 , max);                      }                     for (int i : list)                        fop.append(i + "") ;                                          fop.flush();                    fop.close();                } catch (Exception e) {                    return;                }                    }            /*-----------------------------------------------------------------------------------------------------------------------------------------------*/                    static void sieve(int n) {                boolean[] flag = new boolean[n];                    for (int i = 2; i * i < n; i++) {                    if (flag[i])                        continue;                    else                        for (int j = i * i; j <= n; j += i) {                            flag[j] = true;                        }                }            }                    static int gcd(int a, int b) {                if (b > a) {                    int tenp = b;                    b = a;                    a = tenp;                }                int temp = 0;                while (b != 0) {                    a %= b;                    temp = b;                    b = a;                    a = temp;                }                return a;            }                static long gcd_long(long a, long b) {                if (b > a) {                    long tenp = b;                    b = a;                    a = tenp;                }                long temp = 0;                while (b != 0) {                    a %= b;                    temp = b;                    b = a;                    a = temp;                }                return a;            }                    static final Random random = new Random();                static void ruffleSort(int[] a) {                int n = a.length;//shuffle, then sort                for (int i = 0; i < n; i++) {                    int oi = random.nextInt(n), temp = a[oi];                    a[oi] = a[i];                    a[i] = temp;                }                Arrays.sort(a);            }                    static class FastScanner {                BufferedReader br = new BufferedReader(new InputStreamReader(System.in));                StringTokenizer st = new StringTokenizer("");                    String next() {                    while (!st.hasMoreTokens())                        try {                            st = new StringTokenizer(br.readLine());                        } catch (IOException e) {                            e.printStackTrace();                        }                    return st.nextToken();                }                    int nextInt() {                    return Integer.parseInt(next());                }                    int[] readArray(int n) {                    int[] a = new int[n];                    for (int i = 0; i < n; i++) a[i] = nextInt();                    return a;                }                    long[] readLongArray(int n) {                    long[] a = new long[n];                    for (int i = 0; i < n; i++)                        a[i] = nextLong();                    return a;                }                    long nextLong() {                    return Long.parseLong(next());                }            }                }                                
