import java.util.*;import java.io.*;/** * * @author alanl */public class Main{    static BufferedReader input = new BufferedReader(new InputStreamReader(System.in));    static StringTokenizer st;    static PrintWriter pr = new PrintWriter(new BufferedWriter(new OutputStreamWriter(System.out)));    public static void main(String[] args) throws IOException{        int n = readInt(), m = readInt(), k = readInt();        char[][]adj = new char[n][m];        for(int i = 0; i<n; i++){            String s = readLine();            for(int j = 0; j<m; j++){                adj[i][j] = s.charAt(j);            }        }        for(int j = 0; j<m; j++){            int cnt = 0;            for(int i = 0; i<n; i++){                if((j-i)>=0 && adj[i][j-i]=='R')cnt++;                if((i+j)<m  && adj[i][i+j]=='L')cnt++;                if(adj[i][j]=='U'){                    if((i&1)==0)cnt++;                }            }            print(cnt+" ");	}	println();    }    static String next () throws IOException {        while (st == null || !st.hasMoreTokens())                st = new StringTokenizer(input.readLine().trim());        return st.nextToken();    }    static long readLong () throws IOException {        return Long.parseLong(next());    }    static int readInt () throws IOException {        return Integer.parseInt(next());    }    static double readDouble () throws IOException {        return Double.parseDouble(next());    }    static char readChar () throws IOException {        return next().charAt(0);    }    static String readLine () throws IOException {        return input.readLine().trim();    }    static void print(Object b) {        System.out.print(b);    }    static void println(Object b) {        System.out.println(b);    }    static void println() {        System.out.println();    }}
