//package codeforces; import java.util.ArrayList;import java.util.Scanner; public class CapitalSmall {		public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int v = sc.nextInt();		int count = 0;		ArrayList<Integer> sellers = new ArrayList<>();				for(int i = 0; i < n; i++) {			int items = sc.nextInt();			boolean found = false;			for(int j = 0; j < items; j++) {				int bid = sc.nextInt();				if(v > bid && found == false) {					found = true;					sellers.add(i + 1);					++count;				}			}		}		sc.close();				if(count > 0) {			System.out.println(count);			for(int i = 0; i < sellers.size(); i++) {				System.out.print(sellers.get(i) + " ");			}			System.out.println();		}				else {			System.out.println(0);		}	}}
