import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);				int n=sc.nextInt();		int d=sc.nextInt();		int a[] = new int[n];		int b[] = new int[n];		int i;		for(i=0;i<n;i++){		    a[i] = sc.nextInt();		    b[i]=0;		}				for(i=0;i<n;i++){		    while(a[i]>0){		        b[i]=b[i]+1;		        a[i]=a[i]-d;		    }		}		int c = n-1;		for(i=n-2;i>-1;i--){		    if(b[c]<b[i])c=i;		}	    	    System.out.println(c+1);	}}
