//created by Whiplash99import java.io.*;import java.util.*;public class A{    private static double pow(double a, long b)    {        double res=1;        while (b>0)        {            if((b&1)==1)                res=res*a;            a=a*a;            b>>=1;        }        return res;    }    public static void main(String[] args) throws Exception    {        BufferedReader br=new BufferedReader(new InputStreamReader(System.in));         int i;         String[] s=br.readLine().trim().split(" ");        double M=Integer.parseInt(s[0]);        double N=Integer.parseInt(s[1]);         double prev=0, sum=0;        for(i=1;i<=M;i++)        {            double count=pow(i/M,(long)N);            count-=prev; prev+=count;            sum+=count*i;        }         System.out.println(sum);    }}
