import java.io.*;import java.util.StringTokenizer;// نورت الكود يا كبير اتفضل // يا رب Acceptedpublic class Boredom {    private static final long[] freq = new long[(int) (1e5 + 5)];    private static final long[] dp = new long[(int) (1e5 + 5)];     public static void main(String[] args) {        FastReader in = new FastReader();        PrintWriter out = new PrintWriter(new BufferedOutputStream(System.out));         int n = in.nextInt();        for (int i = 0; i < n; i++) {            int x = in.nextInt();            freq[x]++;        }        dp[1] = freq[1];        for (int i = 2; i < 1e5 + 5; i++) {            dp[i] = Math.max(dp[i - 1], (freq[i] * i) + dp[i - 2]);        }         out.println(dp[(int) (1e5 + 4)]);         out.close();    }     private static class FastReader {        BufferedReader br;        StringTokenizer st;         FastReader() {            br = new BufferedReader(new                    InputStreamReader(System.in));        }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                } catch (IOException e) {                    e.printStackTrace();                }            }            return st.nextToken();        }         int nextInt() {            return Integer.parseInt(next());        }    }}
