#include <bits/stdc++.h> using namespace std; int main(){    ios::sync_with_stdio(false);    cin.tie(0);     int n;    cin >> n;    vector<int> v(n);    long long tsum = 0;    for (auto & e: v) {        cin >> e;        tsum += e;    }        sort(v.begin(), v.end(), greater<int>());    v.push_back(0);     long long ans = -v[0];    while (!v.empty()) {        ans += tsum;        tsum -= v.back();        v.pop_back();    }     cout << ans << endl;     return 0;}
