#!/usr/bin/env python3from sys import stdin  def solve(tc):    n, a, b = map(int, stdin.readline().split())     if a*b >= 6*n:        print(a*b)        print(a, b)        return     swapped = False    mini = int(1e18)+1    minv = (-1, -1)    if a > b:        a, b = b, a        swapped = True     base = 6 * n    ma = a    while True:        if base//ma < b or ma*ma > base:            break         if base % ma == 0:            print(base)            if swapped:                print(base//ma, ma)            else:                print(ma, base//ma)            return         mb = base // ma + 1        if mini > ma*mb - base:            mini = ma*mb - base            minv = (ma, mb)         ma += 1     print(minv[0]*minv[1])    if swapped:        print(minv[1], minv[0])    else:        print(minv[0], minv[1])  tc = 1solve(tc)
