//package codeforces;import java.util.*;public class TriangluarNumbers { 	public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int T = scan.nextInt();		// O(n)		int n = 0;		for (n=0; n<=500; n++ ) {			int x = n*(n+1);			int y = T*2;			if(x == y) {				System.out.println("YES");				return;			} else if (x > y) {				break;			}		}		System.out.println("NO");  	} }
