//package codeforces;import java.util.*;public class TriangluarNumbers { 	public static void main(String[] args) {		Scanner scan = new Scanner(System.in);		int x = scan.nextInt();		int N = 1000; 		int[] A = new int[N];		int adder = 1;  		for(int i = 1; i < N; i++) {			A[i] = A[i - 1] + adder; 			adder++; 		}		//System.out.println(Arrays.toString(A));				boolean res = false; 		for(int i = 1; i < N; i++) {			if(x == A[i]) {				res = true;				break; 			}		}		if(res) {			System.out.println("YES");		}		else {			System.out.println("NO");		}	} }
