import java.util.*;import java.io.*; public class Main{	public static void main(String[] args) {				Scanner sc= new Scanner(System.in);				String k="qwertyuiopasdfghjkl;zxcvbnm,./";				StringBuilder sb=new StringBuilder();				String x =sc.next();		String text=sc.next();		if(x.charAt(0)=='R') {			for(int i=0;i<text.length();i++) {				for(int z=0;z<k.length();z++) {					if(k.charAt(z)==text.charAt(i)) {						sb.append(k.charAt(z-1));					}				}			}		}else {			for(int i=0;i<text.length();i++) {				for(int z=0;z<k.length();z++) {					if(k.charAt(z)==text.charAt(i)) {						sb.append(k.charAt(z+1));					}				}			}		}				System.out.println(sb.toString());		}}
