//package Coding; import java.io.*;import java.util.*; public class CodeForces {    public static void main(String[] args) throws IOException {        Scanner in = new Scanner(System.in);        int n = in.nextInt();        int[][] worms = new int[n][2];        worms[0] = new int[]{1, in.nextInt()};        for (int i = 1; i < n; ++i) {            worms[i][0] = worms[i - 1][1] + 1;            worms[i][1] = worms[i][0] + in.nextInt() - 1;        }         int m = in.nextInt();        for (int i = 0; i < m; ++i) {            int q = in.nextInt();            int low = 0;            int high = n - 1;            int mid = 0;            while (low <= high) {                mid = low + (high - low) / 2;                if (worms[mid][0] <= q && worms[mid][1] >= q) {                    break;                } else if (q < worms[mid][0]) {                    high = mid;                } else {                    low = mid + 1;                }             }            System.out.println(mid  + 1);        }       }  }          
