#include<iostream>#include<vector>using namespace std; int main() {    int n, k;    cin>>n>>k;    vector<int> a;    vector<int> b;    vector<int> c;     int k2 = k+1;    int lim = k2 / 2;    int lim2;    if(k2 % 2 != 0)        lim++;    lim2 = k2 - lim;    int curr = 1;    for(int i=0;i<lim;i++) {        a.push_back(curr);        curr++;    }    curr = k2;    for(int i=0;i<lim2;i++) {        b.push_back(curr);        curr--;    }    curr = k2+1;    for(int i=0;i<n-k2;i++) {        c.push_back(curr);        curr++;    }    int i = 0;    for(i=0;i<a.size() && i<b.size();i++) {        cout<<a[i]<<" "<<b[i]<<" ";    }    if(i<a.size())        cout<<a[i]<<" ";    for(i=0;i<c.size();i++)        cout<<c[i]<<" ";    cout<<'\n';    return 0;}
