// Working program with FastReaderimport java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.StringTokenizer;     public class Main {	static class FastReader {		BufferedReader br;		StringTokenizer st; 		public FastReader()		{			br = new BufferedReader(				new InputStreamReader(System.in));		} 		String next()		{			while (st == null || !st.hasMoreElements()) {				try {					st = new StringTokenizer(br.readLine());				}				catch (IOException e) {				}			}			return st.nextToken();		} 		int nextInt() { return Integer.parseInt(next()); } 		long nextLong() { return Long.parseLong(next()); } 		double nextDouble()		{			return Double.parseDouble(next());		} 		String nextLine()		{			String str = "";			try {				str = br.readLine();			}			catch (IOException e) {			}			return str;		}	}         public static void main(String[] args){            FastReader sc = new FastReader(); 		long l = sc.nextLong();		long r = sc.nextLong();		for (long i=l; i<=r-2; i++) {			if(GCD(i,i+1)==1 && GCD(i+1,i+2)==1 && GCD(i,i+2)!=1){				System.out.println(i+" "+(i+1)+" "+(i+2));				return;			}		}		System.out.println("-1");	} 	private static long GCD(long a, long b) {		if(b==0)			return a;                return GCD(b,a%b);        }    }                                                
