# Below line read inputs from user using map() function def coprime(value1 , value2):    while (value1 != 0 and value2 != 0):        if (value1 > value2):            value1 %= value2        else:            value2 %= value1    if (max(value1, value2) == 1):        return 1    else:        return 0 a = list(map(int, input().strip().split()))[:2]fir = a[0]sec = a[0]+1trd = a[1] if(trd - fir <= 1):    print(-1)else:    for f in range(fir, trd + 1):        for i in range(sec, trd + 1):            if(coprime(i , f) == 0):                continue            for j in range(sec + 1, trd + 1):                if(coprime(j , i) == 0):                    continue                if(coprime(j , f) == 0 and f<=i<=j):                    print(f,i,j)                    exit()    print(-1)
