	// Main Code at the Bottom	import java.util.*;	import java.io.*; 	public class Main{		//Fast IO class	    static class FastReader {	        BufferedReader br; 	        StringTokenizer st; 	        public FastReader() {	        	boolean env=System.getProperty("ONLINE_JUDGE") != null;	        	if(!env) {	        		try {						br=new BufferedReader(new FileReader("src\\input.txt"));					} catch (FileNotFoundException e) {						e.printStackTrace();					}	        	}	        	else br = new BufferedReader(new InputStreamReader(System.in)); 	        } 	        String next() {	            while (st == null || !st.hasMoreElements()) {	                try {	                    st = new StringTokenizer(br.readLine()); 	                } 	                catch (IOException  e) {	                    e.printStackTrace(); 	                } 	            } 	            return st.nextToken(); 	        } 	        int nextInt() {	            return Integer.parseInt(next()); 	        } 	        long nextLong() {	            return Long.parseLong(next()); 	        } 	        double nextDouble() {	            return Double.parseDouble(next()); 	        } 	        String nextLine() {	            String str = ""; 	            try {	                str = br.readLine(); 	            } 	            catch (IOException e) {	                e.printStackTrace(); 	            } 	            return str; 	        } 	    }     	    static long MOD=(long)1e9+7;	    //debug	    static void debug(Object... o) {	        System.out.println(Arrays.deepToString(o));	    }	    // Pair	    static class pair{	    	long x,y;	    	pair(long a,long b){	    		this.x=a;	    		this.y=b;	    	}	    	public boolean equals(Object obj) {	    		if(obj == null || obj.getClass()!= this.getClass()) return false;	            pair p = (pair) obj;	            return (this.x==p.x && this.y==p.y);	        }	    	public int hashCode() {	            return Objects.hash(x,y);	        }	    }	    static FastReader sc=new FastReader();	    static PrintWriter out=new PrintWriter(System.out);  	    //Global variables and functions	    //Main function(The main code starts from here)	    public static void main (String[] args) throws java.lang.Exception {	    	int test=1;	    	//test=sc.nextInt();	    	while(test-->0) {	    		int n=sc.nextInt();	    		int a[]=new int[n];	    		int prev[]=new int[2*1000000+1];	    		for(int i=0;i<n;i++) {	    			a[i]=sc.nextInt();	    			prev[a[i]]=i+1;	    		}	    		for(int i=1;i<=2*1000000;i++) {	    			if(prev[i]==0) prev[i]=prev[i-1];	    		}	    		int ans=0;	    		HashSet<Integer> set=new HashSet<>();	    		for(int i=0;i<n;i++) {	    			if(set.contains(a[i]) || a[i]==1) continue;	    			set.add(a[i]);	    			for(int j=2*a[i];j<=2*1000000;j+=a[i]) {	    				ans=Math.max(ans, a[prev[j-1]-1]%a[i]);	    			}	    		}	    		out.println(ans);	    			    	}		        out.flush();	        out.close();	    }	}
