public class p484B {    public static void main(String[] args) {        java.util.Scanner sc=new java.util.Scanner(System.in);        int n=sc.nextInt(),m=0,l=2000001,a[]=new int[l],pre[]=new int[l];        for(int i=0;i<n;i++) a[sc.nextInt()]++;        for(int i=1;i<l;i++) if(a[i-1]>0) pre[i]=i-1; else pre[i]=pre[i-1];        for(int i=1;i<l;i++) if(a[i]>0) for(int j=i*2;j<l;j+=i) m=Math.max(m,pre[j]%i);        System.out.println(m);    }}
