import java.util.*;public class Practice{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int m = sc.nextInt();		int n = sc.nextInt();		int s=1;		int[][] mas1 = new int[n][m];		int[][] mas2 = new int[n][m];		int mas3;		for (int j=0;j<m;j++){			for(int i=0;i<n;i++){			mas1[i][j] = sc.nextInt();			}		}		for(int j=0;j<m;j++){			for(int i=0;i<n;i++){				mas2[i][j]=mas1[i][j];				for(int k=0;k<m;k++){					mas2[i][j]*=mas1[i][k];				}				for(int k=0;k<n;k++){					mas2[i][j]*=mas1[k][j];				}			}		}		for(int i=0;i<n;i++){			for(int j=0;j<m;j++){				mas3=mas2[i][j];				for(int k=0;k<m;k++){					mas3+=mas2[i][k];				}				for(int k=0;k<n;k++){					mas3+=mas2[k][j];				}				if((mas3*mas1[i][j]>0)||(mas3+mas1[i][j]==0)){									}else{s=0;}			}		}		if(s==0){			System.out.print("NO");		}		else{			System.out.println("YES");			for (int j=0;j<m;j++){				for(int i=0;i<n;i++){				System.out.print(""+mas2[i][j]+" ");				}				System.out.println();			}		}	}}
