import java.util.*;public class Main{    public static void main(String args[])    {        Scanner sc = new Scanner(System.in);        int k,n = sc.nextInt();        int ar[] = new int[n];        int fr[] = new int[4];        Arrays.fill(fr,0);        for(int i=0;i<n;i++)        {            k = sc.nextInt();            ar[i] = k;            fr[k]++;        }        int team = Integer.MAX_VALUE;        for(int i=1;i<=3;i++)        {            if(team>fr[i])            team = fr[i];        }        System.out.println(team);        int f=0,s=0,t=0;        for(k=0;k<team;k++)        {            while(ar[f]!=1)            {                f++;            }            while(ar[s]!=2)            {                s++;            }            while(ar[t]!=3)            {                t++;            }            ++f;            ++s;            ++t;            System.out.println(f+" "+s+" "+t);        }    }}
