import java.util.*;public class VanyaL{public static void main(String[] args){Scanner s=new Scanner(System.in);int n=s.nextInt();int l=s.nextInt();double[] arr=new double[n];for(int i=0;i<n;i++){arr[i]=s.nextInt();}Arrays.sort(arr);double dis=arr[0]-0;for(int i=0;i<n-1;i++){double k=(arr[i+1]-arr[i])/2;if(k>dis){dis=k;}}if((l-arr[n-1])>dis){dis=l-arr[n-1];}System.out.println(dis);}}
