// Main Code at the Bottomimport java.util.*;import java.lang.*;import java.io.*;import java.math.BigInteger; public class Main{	//Fast IO class    static class FastReader {        BufferedReader br;         StringTokenizer st;         public FastReader() {        	boolean env=System.getProperty("ONLINE_JUDGE") != null;        	if(!env) {        		try {					br=new BufferedReader(new FileReader("src\\input.txt"));				} catch (FileNotFoundException e) {					e.printStackTrace();				}        	}        	else br = new BufferedReader(new InputStreamReader(System.in));         }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e) {                    e.printStackTrace();                 }             }             return st.nextToken();         }         int nextInt() {            return Integer.parseInt(next());         }         long nextLong() {            return Long.parseLong(next());         }         double nextDouble() {            return Double.parseDouble(next());         }         String nextLine() {            String str = "";             try {                str = br.readLine();             }             catch (IOException e) {                e.printStackTrace();             }             return str;         }     }         static long MOD=1000000000+7;    //debug    static void debug(Object... o) {        System.out.println(Arrays.deepToString(o));    }    // Pair    static class pair{    	long x,y;    	pair(long a,long b){    		this.x=a;    		this.y=b;    	}    	public boolean equals(Object obj) {    		if(obj == null || obj.getClass()!= this.getClass()) return false;            pair p = (pair) obj;            return (this.x==p.x && this.y==p.y);        }    	public int hashCode() {            return Objects.hash(x,y);        }    }    static FastReader sc=new FastReader();    static PrintWriter out=new PrintWriter(System.out);      //Global variables and functions    //Main function(The main code starts from here)    public static void main (String[] args) throws java.lang.Exception {    	int test=1;    	//test=sc.nextInt();    	while(test-->0){    		int n=sc.nextInt();    		String str=sc.nextLine();    		int a[]=new int[n],ans[]=new int[n];    		for(int i=0;i<n;i++) a[i]=str.charAt(i)-'0';    		Arrays.fill(ans, 9);    		for(int i=0;i<n;i++) {    			int tmp[]=new int[n];    			for(int j=0;j<n;j++) tmp[(j-i+n)%n]=a[j];    			int moves=10-tmp[0];    			for(int j=0;j<n;j++) tmp[j]=(tmp[j]+moves)%10;    			int f=0;    			for(int j=0;j<n;j++) {    				if(tmp[j]==ans[j]) continue;    				if(tmp[j]<ans[j]) f=1;    				break;    			}    			if(f==1) ans=tmp;    		}    		for(int x: ans) out.print(x);    	}	        out.flush();        out.close();    }}
