import java.util.*;public class Main{	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int n = in.nextInt();		int k = in.nextInt();		int a[] = new int[n];		for(int i=0 ; i<n ; i++) a[i]=in.nextInt();		StringBuffer sb = new StringBuffer();		int c=0;        while(k>0)	     {	       int min=Integer.MAX_VALUE , p=-1 ;	       for(int i=0 ; i<n ; i++)	       {	           if(a[i]<min)	           {	               min=a[i];	               p=i;	           }	       }	       k-=min;	       if(k>=0)	       {	        c++;	        a[p]=Integer.MAX_VALUE;	        p=p+1;	        sb.append(p+" ");	       }	                }         System.out.println(c);         System.out.println(sb);	} }
