import java.util.*;// scanner.nextInt();public class Solution {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in); 		int n = scanner.nextInt();                int[][] array = new int[n][4];                                for(int i = 0; i < n;++i)                {                    String t = scanner.next();                    array[i][0] = Integer.parseInt(String.valueOf(t.charAt(0)));                    array[i][1] = Integer.parseInt(String.valueOf(t.charAt(1)));                    //System.out.println( array[i][0] + " "  + array[i][1] );                    t = scanner.next();                    array[i][2] = Integer.parseInt(String.valueOf(t.charAt(0)));                    array[i][3] = Integer.parseInt(String.valueOf(t.charAt(1)));                    //System.out.println( array[i][2] + " "  + array[i][3] );                    if(i < n-1) scanner.next();                }                                int res = 0;                boolean[] check = new boolean[n];                                for(int i = 0; i < n; ++i)                {                    if(!check[i])                    {                        ++res;                        for(int j = i; j < n; ++j)                        {                            if(equal(array[i],array[j])) check[j] = true;                        }                    }                }                System.out.println(res);		         }    public static boolean equal(int[] one, int[] two)    {        boolean res = false;             if(one[0] == two[0] && one[1] == two[1] && one[2] == two[2] && one[3] == two[3]) res = true;        else if(one[0] == two[1] && one[1] == two[3] && one[2] == two[0] && one[3] == two[2]) res = true;        else if(one[0] == two[3] && one[1] == two[2] && one[2] == two[1] && one[3] == two[0]) res = true;        else if(one[0] == two[2] && one[1] == two[0] && one[2] == two[3] && one[3] == two[1]) res = true;        return res;    }}Â 
