import java.util.*;public class Snake{	public static void main(String[] args) {	    Scanner sc=new Scanner(System.in);	    int n=sc.nextInt();	   int m=sc.nextInt();	   for(int i = 1; i <= n; i++)	{		for(int j = 1; j <= m; j++)		{			boolean haveSnake = false;			if(i % 2 == 1) haveSnake = true;			else			{				if(i % 4 == 2) haveSnake = (j == m);				if(i % 4 == 0) haveSnake = (j == 1);			}			 System.out.print(haveSnake ? "#" : ".");		}		System.out.println();	}	   				}}
