import java.util.*; public class B_520{public static void main(String[] args){   Scanner omkar = new Scanner(System.in);   int n = omkar.nextInt();   int m = omkar.nextInt();   int u = gcd(m, n);   int[] b = new int[n];   int[] g = new int[m];   int x = omkar.nextInt();   for(int i =0; i < x; i++)   {      b[omkar.nextInt()] = 1;   }   int y = omkar.nextInt();   for(int i =0; i < y; i++)   {      g[omkar.nextInt()] = 1;   }   for(int i = 0; i < u; i++)   {      if(!test(b, g, u, i))      {         System.out.println("No");         return;      }   }   System.out.println("Yes");        }public static int gcd(int a, int b)     {         if (a == 0)             return b;                   return gcd(b%a, a);     }    public static boolean test(int[] b, int[] g, int u, int ind)    {      for(int i = ind; i < b.length; i += u)      {         if(b[i] == 1)         {            return true;         }      }      for(int i = ind; i < g.length; i += u)      {         if(g[i] == 1)         {            return true;         }      }      return false;    }}
