import java.util.*;public class Pangram{public static void main(String[] args){Scanner s=new Scanner(System.in);int n=s.nextInt();String str=s.next();if(n<26){System.out.println("NO");}else{int[] arr=new int[26];for(int i=0;i<n;i++){char a=str.charAt(i);if(a>=65 && a<=91){	arr[a-65]++;}else if(a>=97 && a<=122){	arr[a-97]++;}}int flag=0;for(int i=0;i<26;i++){if(arr[i]==0){System.out.println("NO");flag=1;break;}}if(flag==0){System.out.println("YES");}}}}
