# Importsfrom typing import List, Dict def lowercase() -> str:	return 'abcdefghijklmnopqrstuvwxyz' n: int = int(input()) # number of room in the house string: str = input() # keys: (i+1) / 2 i: int = 0 available_keys: Dict[str, int] = dict.fromkeys(lowercase(), 0) # Initialize the dict buy: int = 0next:str = ''count:int = 0 while i <= 2*n-4: # Iterate till the second last index of the input string 	available_keys[string[i]] = available_keys[string[i]] + 1		next = string[i+1].lower()		count = available_keys[next]	if count > 0:		available_keys[next] = count - 1	else:		buy = buy + 1 	i = i + 2 print(buy)
