// package CF; import java.io.BufferedReader;import java.io.FileReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.io.PrintWriter;import java.util.StringTokenizer; public class A { 	public static void main(String[] args) throws Exception {		Scanner sc = new Scanner(System.in);		PrintWriter out = new PrintWriter(System.out);		int n = sc.nextInt();		char [] c = sc.next().toCharArray();		for (int i = 1; i < c.length; i++) {			for (int j = 0; j < c.length; j++) {				int j2 = 0;				for (int k = j; j2 < 5 && k < c.length; j2++, k += i) {					if(c[k] == '.')						break;				}				if(j2 == 5)				{					System.out.println("yes");					return;				}			}		}		out.println("no");		out.flush();		out.close();	} 	static class Scanner {		StringTokenizer st;		BufferedReader br; 		public Scanner(InputStream s) {			br = new BufferedReader(new InputStreamReader(s));		} 		public Scanner(FileReader fileReader) {			br = new BufferedReader(fileReader);		} 		public String next() throws IOException {			while (st == null || !st.hasMoreTokens())				st = new StringTokenizer(br.readLine());			return st.nextToken();		} 		public int nextInt() throws IOException {			return Integer.parseInt(next());		} 		public long nextLong() throws IOException {			return Long.parseLong(next());		} 		public String nextLine() throws IOException {			return br.readLine();		} 		public boolean ready() throws IOException {			return br.ready();		}	}}
