import java.util.*;public class Main{		 public static void main(String args[])    {        Scanner in  = new Scanner(System.in);        int n = in.nextInt();        int f[] = new int[n+1];        for(int i=1 ; i<=n ; i++)        {            for(int j=1 ; j<=n ; j++)            {                int x=in.nextInt();                  if(x==1)                     f[i]++;                  else if(x==2)                      f[j]++;                   else if(x==3)                      {                          f[i]++;                          f[j]++;                      }            }        }        String s="";        int ans=0;        for(int i=1 ; i<=n ; i++)        {            if(f[i]==0)            {                s=s+String.valueOf(i)+" ";                ans++;            }        }        if(ans==0) System.out.println(ans);        else System.out.println(ans+"\n"+s);            }}
