// practice with rainboyimport java.io.*;import java.util.*; public class CF549B extends PrintWriter {	CF549B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF549B o = new CF549B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		byte[][] cc = new byte[n][];		for (int i = 0; i < n; i++)			cc[i] = sc.next().getBytes();		int[] aa = new int[n];		for (int i = 0; i < n; i++)			aa[i] = sc.nextInt();		int[] qq = new int[n]; int cnt = 0;		while (true) {			int i = 0;			while (i < n && aa[i] != 0)				i++;			if (i == n)				break;			qq[cnt++] = i;			for (int j = 0; j < n; j++)				aa[j] -= cc[i][j] - '0';		}		println(cnt);		while (cnt-- > 0)			print(qq[cnt] + 1 + " ");		println();	}}
