// practice with rainboyimport java.io.*;import java.util.*; public class CF553B extends PrintWriter {	CF553B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF553B o = new CF553B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		long k = sc.nextLong();		long[] ff = new long[n + 1];		ff[0] = ff[1] = 1;		for (int i = 2; i <= n; i++)			ff[i] = ff[i - 2] + ff[i - 1];		int a = 0;		while (n > 0)			if (k <= ff[n - 1]) {				print((a + 1) + " ");				a++;				n--;			} else {				k -= ff[n - 1];				print((a + 2) + " " + (a + 1) + " ");				a += 2;				n -= 2;			}		println();	}}
