# Alexis Naoki Sosa Aoyama 4.713.367 Algoritmos 2 2021def EquivalentStrings(a, b):    if a == b:        return True     if len(a) % 2 != 0:        return a == b     halve_len = int(len(a)/2)     a1 = a[:halve_len]    a2 = a[halve_len:]     b1 = b[:halve_len]    b2 = b[halve_len:]     if EquivalentStrings(a1, b2) and EquivalentStrings(a2, b1):        return True     if EquivalentStrings(a1, b1) and EquivalentStrings(a2, b2):        return True     return False  a = input()b = input() if EquivalentStrings(a, b):    print('YES')else:    print('NO')
