# Alexis Naoki Sosa Aoyama 4.713.367 Algoritmos 2 2021def EquivalentStrings(a, b):    if len(a) % 2 != 0:        return a == b     if a == b:        return True     half = int(len(a)/2)    a1 = a[:half]    a2 = a[half:]     b1 = b[:half]    b2 = b[half:]    if (EquivalentStrings(a1, b2) and EquivalentStrings(a2, b1)):        return True    if (EquivalentStrings(a1, b1) and EquivalentStrings(a2, b2)):        return True    return False  a = input()b = input() if EquivalentStrings(a, b):    print('YES')else:    print('NO')
