// practice with rainboyimport java.io.*;import java.util.*; public class CF568A extends PrintWriter {	CF568A() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF568A o = new CF568A(); o.main(); o.flush();	} 	static final int N = 1179858;	int[] dd = new int[8];	boolean palin(int n) {		int k = 0;		while (n > 0) {			dd[k++] = n % 10;			n /= 10;		}		for (int i = 0, j = k - 1; i < j; i++, j--)			if (dd[i] != dd[j])				return false;		return true;	}	void main() {		int p = sc.nextInt();		int q = sc.nextInt();		int[] kk = new int[N + 1];		Arrays.fill(kk, 1);		kk[0] = kk[1] = 0;		for (int a = 2; a <= N; a++) {			if (kk[a] == 0) {				kk[a] += kk[a - 1];				continue;			}			kk[a] += kk[a - 1];			for (int b = a + a; b <= N; b += a)				kk[b] = 0;		}		int[] ll = new int[N + 1];		int l = 0;		for (int n = 1; n <= N; n++) {			if (palin(n))				l++;			ll[n] = l;		}		for (int n = N; n >= 0; n--)			if ((long) kk[n] * q <= (long) p * ll[n]) {				println(n);				return;			}	}}
