// practice with rainboyimport java.io.*;import java.util.*; public class CF568B extends PrintWriter {	CF568B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF568B o = new CF568B(); o.main(); o.flush();	} 	static final int MD = 1000000007;	void main() {		int n = sc.nextInt();		int[][] ch = new int[n + 1][n + 1];		ch[0][0] = 1;		for (int i = 1; i <= n; i++) {			ch[i][0] = 1;			for (int j = 1; j <= i; j++)				ch[i][j] = (ch[i - 1][j] + ch[i - 1][j - 1]) % MD;		}		int[] dp  = new int[n + 1];		dp[0] = 1;		for (int i = 1; i <= n; i++) {			long x = 0;			for (int j = 2; j <= i; j++) {				long y = dp[i - j];				if (y != 0)					x = (x + y * ch[i - 1][j - 1]) % MD;			}			dp[i] = (int) x;		}		int[] pp = new int[n + 1];		for (int i = 0, p = 1; i <= n; i++) {			pp[i] = p - 1;			p = p * 2 % MD;		}		long ans = 0;		for (int k = 1; k <= n; k++)			ans = (ans + (long) pp[k] * ch[n][k] % MD * dp[n - k]) % MD;		println(ans);	}}
