// practice with rainboyimport java.io.*;import java.util.*;Â public class CF571A extends PrintWriter {	CF571A() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF571A o = new CF571A(); o.main(); o.flush();	}Â 	long count2(int h) {		return h % 2 == 1 ? (long) (h + 1) / 2 * ((h + 1) / 2 + 1)			: (long) (h / 2 + 1) * (h / 2 + 1);	}	long count1(int h, int r) {		return count2(h) - (r >= h ? 0 : count2(h - 1 - r));	}	long count(int h, int k, int r) {		return count1(h, r) - (k == 0 ? 0 : count1(k - 1, r));	}	long solve(int a, int b, int c, int h) {		if (a + 1 > b + c + h)			return 0;		int k = a + 1 <= b + c ? 0 : a + 1 - (b + c);		int l = 1 - a - (c - b);		int r = a - 1 - (c - b);		if (l >= 0)			return count(h, k, r) - (l == 0 ? 0 : count(h, k, l - 1));		if (r <= 0) {			r = -r; l = -l;			return count(h, k, l) - (r == 0 ? 0 : count(h, k, r - 1));		}		return count(h, k, -l) + count(h, k, r) - count(h, k, 0);	}	void main() {		int a = sc.nextInt();		int b = sc.nextInt();		int c = sc.nextInt();		int l = sc.nextInt();		long ans = 0;		for (int l_ = 0; l_ <= l; l_++)			ans += solve(a + l_, b, c, l - l_);		println(ans);	}}
