//I AM THE CREED/* //I AM THE CREED/* package codechef; // don't place package name! */import java.io.BufferedReader; import java.io.IOException; import java.io.InputStreamReader; import java.util.StringTokenizer; import java.util.*;import java.awt.Point;public class Main{        public static void main(String[] args) throws IOException     {                 FastScanner input = new FastScanner();        int n=input.nextInt();        StringBuilder sol=new StringBuilder();        int size=0;        for(int i=2;i<=n;i++){            if(i==2 || isPrime(i)){                for(int j=i;j<=n;j*=i){                    sol.append(j+" ");                    size++;                }            }        }        System.out.println(size);        System.out.println(sol.toString());    }    static boolean isPrime(int n)     {         // Corner case         if (n <= 1)             return false;           // Check from 2 to n-1         for (int i = 2; i < n; i++)             if (n % i == 0)                 return false;           return true;     }     static class FastScanner {		BufferedReader br=new BufferedReader(new InputStreamReader(System.in));		StringTokenizer st=new StringTokenizer("");		String next() {			while (!st.hasMoreTokens())				try {					st=new StringTokenizer(br.readLine());				} catch (IOException e) {					e.printStackTrace();				}			return st.nextToken();		}				int nextInt() {			return Integer.parseInt(next());		}		int[] readArray(int n) {			int[] a=new int[n];			for (int i=0; i<n; i++) a[i]=nextInt();			return a;		}		long nextLong() {			return Long.parseLong(next());		}	} }
