// practice with rainboyimport java.io.*;import java.util.*; public class CF576B extends PrintWriter {	CF576B() { super(System.out); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF576B o = new CF576B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int[] pp = new int[n];		for (int i = 0; i < n; i++)			pp[i] = sc.nextInt() - 1;		boolean[] used = new boolean[n];		int k_ = 3, c1 = -1, c2 = -1; boolean odd = false;		for (int i = 0; i < n; i++) {			if (used[i])				continue;			int k = 0;			for (int j = i; !used[j]; j = pp[j]) {				used[j] = true;				k++;			}			if (k_ > k) {				k_ = k;				if (k == 1)					c1 = i;				else {					c1 = i;					c2 = pp[i];				}			}			if (k % 2 == 1)				odd = true;		}		if (k_ == 3 || k_ == 2 && odd) {			println("NO");			return;		}		println("YES");		if (k_ == 1) {			for (int i = 0; i < n; i++)				if (pp[i] != c1)					println((c1 + 1) + " " + (pp[i] + 1));		} else {			println((c1 + 1) + " " + (c2 + 1));			Arrays.fill(used, false);			used[c1] = used[c2] = true;			for (int i = 0; i < n; i++) {				if (used[i])					continue;				int k = 0;				for (int j = i; !used[j]; j = pp[j]) {					used[j] = true;					println((++k % 2 == 0 ? c1 + 1 : c2 + 1) + " " + (j + 1));				}			}		}	}}
