// practice with rainboyimport java.io.*;import java.util.*; public class CF578A extends PrintWriter {	CF578A() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF578A o = new CF578A(); o.main(); o.flush();	} 	void main() {		int x = sc.nextInt();		int y = sc.nextInt();		if (x < y) {			println(-1);			return;		}		if (x == y) {			println(y);			return;		}		double z1 = (double) (x + y) / ((x + y) / (2 * y) * 2);		double z2 = (double) (x - y) / ((x - y) / (2 * y) * 2);		printf("%.9f\n", Math.min(z1, z2));	}}
