import java.util.*;public class AChatRoom{    public static void main(String[] args)     {        Scanner input = new Scanner(System.in);        String s = input.next();        String t = "hello";        int j = 0;        for(int i = 0; i<s.length();i++)        {            if(j<t.length()&&s.charAt(i)==t.charAt(j))                j++;        }        if(j==t.length())            System.out.println("YES");        else            System.out.println("NO");    }   }
