import java.util.*;import java.lang.*;import java.io.*;public class KefaAndFirstSteps{	public static void main(String args[])throws IOException{		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));		BufferedWriter bw = new BufferedWriter(new OutputStreamWriter(System.out));		int n = Integer.parseInt(br.readLine());        String arr[] = br.readLine().trim().split("\\s+");        int i=0,j=1;        if(n==1){            bw.write("1");        }        else{            int max = Integer.MIN_VALUE;            int count = 1;            for(i=0;i<n-1;i++){                int val1 = Integer.parseInt(arr[i]);                int val2 = Integer.parseInt(arr[i+1]);                if(val1<=val2){                    count++;                }                else{                    count=1;                }                if(count>max){                    max = count;                }            }            bw.write(max+"\n");        }		bw.close();	}}
