//package albert.codeforces.kefaandcompany580b; import java.util.ArrayList;import java.util.Comparator;import java.util.List;import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner in = new Scanner(System.in);        int n = in.nextInt();        int d = in.nextInt();        List<F> friends = new ArrayList<>(n);        for (int i = 0; i < n; i++) {            friends.add(new F(in.nextInt(), in.nextInt()));        }        friends.sort(Comparator.comparingInt(o -> o.money));        long[] prefix = new long[n];        prefix[0] = friends.get(0).factor;        for (int i = 1; i < n; i++) {            prefix[i] = prefix[i - 1] + friends.get(i).factor;        }        long max = 0;        for (int i = 0; i < n; i++) {            int lo = i;            int hi = n - 1;            int target = friends.get(i).money + d - 1;            while (lo <= hi) {                int mid = lo + (hi - lo) / 2;                if (friends.get(mid).money > target) {                    hi = mid - 1;                } else {                    lo = mid + 1;                }            }            long ans = prefix[lo - 1];            if (i > 0) {                ans -= prefix[i - 1];            }            max = Math.max(max, ans);        }        System.out.println(max);    }     static class F {        int money;        int factor;         public F(int money, int factor) {            this.money = money;            this.factor = factor;        }    }}
