import java.util.*; public class CodeForces{    static Scanner input= new Scanner (System.in);        public static void main (String args []){ String s =input.next(); String ss = ""; String sk =""; for (int i = 0; i < s.length(); i++) {        if(s.charAt(i)== s.toLowerCase().charAt(i))            ss+=s.charAt(i);        else            sk+=s.charAt(i);    }     if (ss.length() >= sk.length())        System.out.println(s.toLowerCase()); else         System.out.println(s.toUpperCase());}}
