import java.util.Scanner;public class java{public static void main(String[] args){    Scanner s=new Scanner(System.in);    String ch=s.next();    int k=0;    int m=0;    for (int i=0;i<(ch.length());i++){        if (ch.charAt(i) >= 'a' && ch.charAt(i) <= 'z')            k=k+1;        else             m=m+1;}        if (m>k)         System.out.println(ch.toUpperCase());        else          System.out.println(ch.toLowerCase());}}
