import java.util.Scanner;public class Word{    public static void main(String[] args){        Scanner s = new Scanner(System.in);        String ch=s.next();        int i=0;        int j=0;        char c;        for(int r=0; r < ch.length();r++){            c= ch.charAt(r);            if(Character.isUpperCase(c)){                i++;            }            else{            j++;}        }        if(j>=i){            ch = ch.toLowerCase();        }        else{            ch = ch.toUpperCase();        }        System.out.println(ch);    }}
