import java.util.Scanner;  // Import the Scanner class public class HelloWorld{          public static void main(String []args){        Scanner myObj = new Scanner(System.in);        String ch= myObj.nextLine();         int upper=0;        int test;        for (int i=0 ;i<ch.length();i++){            test=(int)ch.charAt(i);            if ((test<=90) && (test>=65)) {                upper++;            }        }        int div;        if (ch.length()%2==0){            div=ch.length()/2;        }        else {            div=(ch.length()/2 );        }        if (upper<=div){            System.out.println(ch.toLowerCase());        }        else {            System.out.println(ch.toUpperCase());        }             }}
