//package codeforces.div2; import java.util.Scanner; public class Divisibility {     static public void print(long a, long b, long k) {        boolean fl = false;        if(a > 0 && b > 0) {             fl = true;            long x = b/k;            long y = a/k;            if(a%k == 0) {                System.out.println(x - y + 1);            } else {                System.out.println(x - y);            }         } else {            if(a < 0 && b < 0) {                fl = true;                long x = b/k;                long y = a/k;                if(b%k == 0) {                    long z = Math.abs(Math.abs(x) - Math.abs(y)) + 1;                    System.out.println(z);                } else {                    long z = Math.abs(Math.abs(x) - Math.abs(y));                    System.out.println(z);                }            } else {                if(a<=0 && b > 0) {                    fl = true;                    long x = b/k;                    long y = a/k;                    if(a%k == 0) {                        System.out.println(Math.abs(x) + Math.abs(y) + 1);                    } else {                        System.out.println(Math.abs(x) +                                Math.abs(y) + 1);                    }                } else {                    if(a<=0 && b == 0) {                        fl = true;                        System.out.println(Math.abs(a/k) + 1);                    }                }            }        }        if(fl == false) {            System.out.println(1);        }        //System.out.println();    }    public static void main(String []args) {        Scanner sc  = new Scanner(System.in);        long k = sc.nextLong();        long a = sc.nextLong();        long b = sc.nextLong();        print(a, b, k);    }}
