import java.util.Scanner;public class test43{    public static long testCase43(long k,long a,long b) {        long result=0;         if((a==0&&b==0)||(a==b&&a%k==0))            result= 1;        else if((b>0&&a>0)||(b<0&&a<0)){            if((a%k==0||b%k==0)&&k!=1){                result= (b-a)/k+1;            }            else               result= (b-a+1)/k;        }        else if(a==0&&b>0)            result= (b/k)+1;        else if((b==0&&a<0))        result= (-a/k)+1;        else if(a<0&&b>0)          result= (b/k)+(-a/k)+1;          return result;    }    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        long k = input.nextLong();        long a = input.nextLong();        long b = input.nextLong();        System.out.println(testCase43(k,a,b));            }}
